$(function(){let $input=$(".reset-pass-email"),$buffer=$(".input-buffer");let btn=$(".reset-pass-next");$(".input-field").on("input",function(){if($(this).val().length>0)btn.prop({disabled:false});else btn.prop({disabled:true})});$input.on("input",function(){$buffer.text($input.val());$input.width($buffer.width());sessionStorage.setItem("emailForResetPass",$input.val())});$("#logo, #login, #logout, #register").click(function(){if(sessionStorage.getItem("emailForResetPass").length){sessionStorage.removeItem("emailForResetPass")}})});